def plot_cdfs(data_dict:dict):
    """
    各keyに対応する到着時間リストのCDFを描画する関数

    Parameters:
    - data_dict: dict
        例: {0.2: 'arrival_time_list: [96.0, 109.0, ...]', 0.4: 'arrival_time_list: [...]', ...}
    """
    plt.figure(figsize=(10, 6))

    for key, value in data_dict.items():

        # データをソートしてCDFを計算
        value = np.sort(value)
        cdf = np.arange(1, len(value)+1) / len(value)
        # if key == 0.16:
        #     # プロット
        #     plt.plot(value, cdf, label=f'key={key}', color='r', linestyle='-')
        # if key == 0.0:
        #     # プロット
        #     plt.plot(value, cdf, label=f'key={key}', color='b', linestyle='--')
        # plt.plot(value, cdf, label=f'key={key}', color='r', linestyle='-')
        # if key == 0.54:
        #     # プロット
        #     plt.plot(value, cdf, label=f'key={key}', color='g', linestyle='-')
        # if key == 0.5:
        #     plt.plot(value, cdf, label=f'key={key}', color='blue', linestyle='-')
        # if key == 0.0:
        #     plt.plot(value, cdf, label=f'key={key}', color='blue', linestyle='--')
        if key == 0.1:
        # プロット
            plt.plot(value, cdf, label=f'key={key}', color='r', linestyle='-')
        if key == 0.9:
        # プロット
            plt.plot(value, cdf, label=f'key={key}', color='g', linestyle='-')
        if key == 0.5:
            plt.plot(value, cdf, label=f'key={key}', color='blue', linestyle='-')
        if key == 0.0:
            plt.plot(value, cdf, label=f'key={key}', color='blue', linestyle='--')
    plt.xlim(200,1300)
    plt.ylim(0.05,1.0)
    plt.xticks(ticks=np.arange(200, 1310, 200), fontsize=20, fontweight='roman')  
    plt.yticks(ticks=np.arange(0.1, 1.1, 0.1),  fontsize=20, fontweight='roman') 


